import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Box, Typography, Button, Tabs, Tab, Form, Paper } from '@mui/material';
import { cartSlice } from '../../store/cartSlice';
import DeleteTwoTone from '@mui/icons-material/DeleteTwoTone';
import '../../assets/style/checkout.css'

function Checkout() {
  const dispatch = useDispatch();

  function deleteItem(id) {
    dispatch(cartSlice.actions.deleteItem(id))
  }

  const cartItems = useSelector(state => state.cart.cartItems);
  console.log('items in checkout---->', cartItems)
  let cartArr = [];
  if (cartItems.length > 0) {
    cartArr = cartItems.map(item => (
      <>
        <Box sx={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between' }}>
          <Typography>
            {item.name}
          </Typography>
          <Typography>
            {item.price}
          </Typography>
        </Box>
        <Button sx={{ padding: '10px', borderRadius: '7px' }} onClick={() => deleteItem(item.id)}><DeleteTwoTone sx={{
          color: 'salmon', "& button:focus": { color: "salmon" },
          "& button:active": { color: "black" }
        }} /></Button>
        <div id='line-1' >
          <hr />

        </div>
      </>
    ))
  }
  return (


    <Box sx={{ width: '70%', minHeight: '72vh', padding: '10px', margin: '10px' }}>
      <form >
        <Paper sx={{ padding: '10px' }}>
          <Typography sx={{ fontWeight: 'bold', fontSize: '40px' }}>
            Order Summary
          </Typography>
          {cartArr}

        </Paper>
      </form>
    </Box>

  )
}

export default Checkout;
