import { createSlice } from '@reduxjs/toolkit';
import { data as categoryList } from '../assets/dummyData/data';



const initialState = {
  category: '',
  categoryList,
  inventory: ''
}

export const productSlice = createSlice({
  name: 'products',
  initialState,
  reducers: {
    selectCategory(state, action) {

      state.category = action.payload;
      state.categoryList = state.category === 'all' ? categoryList : categoryList.filter
        ((x) => x.category === state.category);
    },
    productInventory(state, action) {
      console.log('product inventory', action.payload - 1)
      state.inventory = state.inventory = action.payload - 1;
    }


  }
});

export const { selectCategory, productInventory } = productSlice.actions;

export default productSlice.reducer;
