import React from 'react'
import Link from '@mui/material/Link';
import Box from '@mui/material/Box';
import { useSelector } from 'react-redux'
import ShoppingCartRoundedIcon from '@mui/icons-material/ShoppingCartRounded';
import '../../assets/style/header.css'

export default function Header() {

  const cartCount = useSelector(state => state.cart.count)
  console.log('count', cartCount)
  const clearLocal = (e) => {
    localStorage.clear();
  }

  return (

    <Box className='headerBox' maxWidth='l' sx={{ p: 2, borderRadius: '7px ', border: 'none' }}>
      <Link fontSize={'40px'} href="/" underline="hover" sx={{ color: 'grey' }} onClick={clearLocal}>
        THE SHOP
      </Link>
      <Link href="/checkout" underline="hover" sx={{ color: 'grey', display: 'flex' }}>
        <ShoppingCartRoundedIcon fontSize='medium' />({cartCount})
      </Link>
    </Box>
  )
}
