import React from 'react'
import { useSelector } from 'react-redux'
import { Box, Button, Card, CardMedia, Stack, Typography } from '@mui/material';

const styles = {
  box: {

    padding: '20px'
  },
  card: {
    margin: '20px',
    padding: '20px', borderRadius: '7px', display: 'flex', alignItems: 'center', justifyContent: 'center',
    boxShadow: 'rgba(0, 0, 0, 0.25) 0px 54px 55px, rgba(0, 0, 0, 0.12) 0px -12px 30px, rgba(0, 0, 0, 0.12) 0px 4px 6px, rgba(0, 0, 0, 0.17) 0px 12px 13px, rgba(0, 0, 0, 0.09) 0px -3px 5px;'
  }
}

function Products() {
  const products = useSelector(state => state.products.categoryList);
  let productArr = [];
  console.log('arr', productArr)
  if (products.length > 0) {
    productArr = products.map(item => (
      <Card key={item.id} sx={styles.card}>
        <CardMedia image={item.image} sx={{ height: '180px', width: '180px' }} />
        <Box sx={styles.box}>
          <Typography>
            {item.name}
          </Typography>
          <hr />
          <Typography>
            Cost: {item.price}
          </Typography>
          <Stack direction="row" spacing={2}>
            <Button sx={{ color: 'salmon' }}>Add to Cart</Button>
            <Button sx={{ color: 'lightblue' }}>Details</Button>
          </Stack>
        </Box>


      </Card>
    ))
  }
  return (
    <Box sx={{ border: '2px solid Yellow', display: 'flex', flexWrap: 'wrap', alignItems: 'left', justifyContent: 'center', padding: '20px' }}>
      {productArr}
    </Box>
  )
}
export default Products;
