import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
// import { store } from '../../store/store'
import { Box, Card, Typography, Button } from '@mui/material';
import { cartSlice } from '../../store/cartSlice';


function Simplecart() {
  const dispatch = useDispatch();
  const cartItems = useSelector(state => state.cart.cartItems);
  // const state = store.getState();
  // console.log('STATE', state.cart.cartItems)
  function deleteItem(id) {


    dispatch(cartSlice.actions.deleteItem(id))

  }

  let cartArr = [];

  if (cartItems.length > 0) {
    cartArr = cartItems.map(item => (
      <Card key={item.id} sx={{ display: 'flex', flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between', borderRadius: '7px', padding: '10px', minWidth: '200px' }}>
        <Box >
          <Typography>
            {item.name}
          </Typography>
        </Box>
        <Button size='none' sx={{ width: '10px', padding: '10px', backgroundColor: 'lightblue', borderRadius: '7px' }} onClick={() => deleteItem(item.id)}>X</Button>
      </Card>
    ))
  }
  return (
    <Box sx={{ position: 'absolute', zIndex: '10', left: '1400px', top: '80px' }}>
      {cartArr}
    </Box>
  )
}

export default Simplecart;
