import React from 'react'
import { useSelector } from 'react-redux'
import { Box, Card, CardMedia, Typography } from '@mui/material';

function Products() {
  const products = useSelector(state => state.products.categoryList);
  let productArr = [];
  console.log('arr', productArr)
  if (products.length > 0) {
    productArr = products.map(item => (
      <Card key={item.id} sx={{ border: '2px solid black', padding: '20px' }}>
        <CardMedia image={item.image} sx={{ height: '180px', width: '180px' }} />
        <Box sx={{ padding: '20px' }}>
          <Typography>
            item: {item.name}
          </Typography>
          <Typography>
            {item.price}
          </Typography>
        </Box>
      </Card>
    ))
  }
  return (
    <Box sx={{ border: '2px solid Yellow', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '20px' }}>
      {productArr}
    </Box>
  )
}
export default Products;
