import { createSlice } from '@reduxjs/toolkit';
import { data as categoryList } from '../assets/dummyData/data';
import Chance from 'chance';
const chance = new Chance();




const initialState = {
  productSelected: categoryList,
  category: '',
  categoryList,
  inventory: ''
}

export const productSlice = createSlice({
  name: 'products',
  initialState,
  reducers: {
    selectCategory(state, action) {

      state.category = action.payload;

      state.productSelected = state.category === 'all' ? state.categoryList : state.categoryList.filter
        ((x) => x.category === state.category);
    },


    productDecrement(state, action) {
      for (const product of state.categoryList) {
        if (product.id === action.payload.id) {
          if (product.inventory !== 0) {
            product.inventory--;
            product.id = chance.bb_pin();

          } else {
            product.inventory = 'out of stock'
          }

        }
        state.productSelected = state.category === 'all' ? state.categoryList : state.categoryList.filter
          ((x) => x.category === state.category);
      }


    },



  }
});

export const { selectCategory, productDecrement } = productSlice.actions;

export default productSlice.reducer;
