import React from 'react';
import { useSelector } from 'react-redux';
import { Box, Card, CardMedia, Typography, Accordion, AccordionSummary, AccordionDetails } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import '../../assets/style/detail.css'

function Details() {


  const singleDetailItem = useSelector(state => state.detail.detailItems);
  let results = [];
  console.log('displayResults', singleDetailItem)
  if (singleDetailItem.length > 0) {
    results = singleDetailItem.map(item => (
      <div>
        <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignContent: 'center', fontSize: '60px', marginTop: '50px' }}> {item.name}</Box>
        <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', marginTop: '50px', width: '600px' }}>
          <Card key={item.id} sx={{ borderRadius: '7px', flexDirection: 'column', }} elevation={5}>
            <CardMedia image={item.image} sx={{ height: '500px', width: '500px', borderRadius: '4px' }} />
            <div id='detailInfo'>
              <Typography sx={{ fontWeight: 'bold', fontSize: '20px' }}>
                {item.category}
              </Typography>
              <hr />
              <Typography >
                Cost: {item.price}
              </Typography>
              <>````````````````````````</>
            </div>
          </Card>
          <Typography variant="subtitle1" sx={{ fontSize: '40px' }}>
            Product Details
          </Typography>
          <Accordion sx={{ borderRadius: '5px' }} >
            <AccordionSummary
              expandIcon={<ExpandMoreIcon />}
              aria-controls="panel1a-content"
              id="panel1a-header"
            >
              <Typography sx={{ fontWeight: 'bold', fontSize: '15px' }}>Product Description</Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography>
                {item.description}
              </Typography>
            </AccordionDetails>
          </Accordion>
          <Accordion elevation={5} sx={{ borderRadius: '5px' }}>
            <AccordionSummary
              expandIcon={<ExpandMoreIcon />}
              aria-controls="panel2a-content"
              id="panel2a-header"
            >
              <Typography sx={{ fontWeight: 'bold', fontSize: '15px' }}>Inventory</Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography>
                total: {item.inventory}
              </Typography>
            </AccordionDetails>
          </Accordion>
        </Box >
      </div>
    ))
  }
  return (
    <Box sx={{}}>
      {results}
    </Box>
  )


}

export default Details;
