import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Box, Typography, Tabs, Tab, Form, Paper } from '@mui/material';
import { Button } from '@cloudscape-design/components';

function Checkout() {
  const dispatch = useDispatch();

  function deleteItem(id) {
    dispatch(cartSlice.actions.deleteItem(id))
  }

  const cartItems = useSelector(state => state.cart.cartItems);
  console.log('items in checkout---->', cartItems)
  let cartArr = [];
  if (cartItems.length > 0) {
    cartArr = cartItems.map(item => (
      <>
        <Typography>
          {item.name}
        </Typography>
        <Button>X</Button>
      </>
    ))
  }
  return (


    <Box sx={{ width: '70%', minHeight: '72vh' }}>
      <form >
        <Paper >
          <Typography sx={{ fontWeight: 'bold', fontSize: '40px' }}>
            Order Summary
          </Typography>
          {cartArr}
        </Paper>
      </form>
    </Box>

  )
}

export default Checkout;
