      <Typography variant="h6" gutterBottom className={classes.title}>
        Payment details
      </Typography>
      <TextField id="cc_number" name="cc_number" label="Credit Card #" />
      <TextField
        id="date"
        label="Expiration"
        type="date"
        InputLabelProps={{
          shrink: true,
        }}
      />
      <TextField id="cvv" name="cvv" label="CVV" />
