import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  detailItems: [],
}

export const detailSlice = createSlice({
  name: 'detail',
  initialState,
  reducers: {

    showDetail(state, action) {
      // console.log('action.payload-DETAIL', action.payload);
      state.detailItems.push(action.payload);
      let storage = JSON.stringify(state.detailItems);
      localStorage.setItem('detailedPageItem', storage)
    },

  }
});
export const { showDetail } = detailSlice.actions;

export const selectedItem = (state) => state.detail;

export default detailSlice.reducer;
