import React from 'react';
import { useSelector } from 'react-redux';
import { Box, Card, CardMedia, Typography, Accordion, AccordionSummary, AccordionDetails } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import '../../assets/style/detail.css'

function Details() {


  const singleDetailItem = useSelector(state => state.detail.detailItems);
  let results = [];
  console.log('displayResults', singleDetailItem)
  if (singleDetailItem.length > 0) {
    results = singleDetailItem.map(item => (
      // <Box sx={{ display: 'flex' }}>
      <Card key={item.id} sx={{ borderRadius: '2px', padding: '10px' }}>
        <CardMedia image={item.image} sx={{ height: '500px', width: '500px', borderRadius: '4px' }} />
        <div id='detailInfo'>
          <Typography>
            {item.name}
          </Typography>
          <hr />
          <Typography>
            {item.price}
          </Typography>
          <>````````````````````````</>
        </div>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1a-content"
            id="panel1a-header"
          >
            <Typography>Product Description</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <Typography>
              {item.description}
            </Typography>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel2a-content"
            id="panel2a-header"
          >
            <Typography>Inventory</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <Typography>
              {item.inventory}
            </Typography>
          </AccordionDetails>
        </Accordion>
      </Card>
      // </Box >
    ))
  }
  return (
    <Box sx={{}}>
      {results}
    </Box>
  )


}

export default Details;
